import React, {useEffect, useState} from "react";
import axios from "axios";
import {Line} from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    
  } from "chart.js";

  ChartJS.register(
    CategoryScale,
    LineElement, 
    CategoryScale, 
    LinearScale, 
    PointElement
  );

  interface CryptoData{
    id: string;
    symbol: string;
    name: string;
    price: number;
    change: number;
    market_cap: number;
    volume_24h: number;
    supply: number;
  }

  const CryptoTrack : React.FC = () => {
    const [cryptoData, setCryptoData] = useState<CryptoData[]>([]);

    useEffect(() => {
        axios.get<CryptoData[]>('http://localhost:8080/api/crypto').then((response) => {
            setCryptoData(response.data);
        }).catch((error) => {
            console.error('Error fetching required data: ', error);
        });
    }, []);

    return (
      <div className="crypto-table">
        <h1>Market</h1>
        <table>
            <thead>
                <tr>
                    <th>Currency</th>
                    <th>Price</th>
                    <th>Change</th>
                    <th>Market Cap</th>
                    <th>Volume 24h</th>
                    <th>Supply</th>
                    <th>Chart</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {cryptoData.map((crypto) => (
                    <tr key={crypto.id}>
                        <td>{crypto.name}</td>
                        <td>${crypto.price
                        .toLocaleString()}</td>
                        <td style={{color:crypto
                            .change >= 0 ? 'green' : 'red'}}>{crypto
                            .change.toFixed(2)}%</td>
                        <td>${(crypto.market_cap / 1e9).toFixed(2)} B</td>
                        <td>${(crypto.volume_24h / 1e9).toFixed(2)} B</td>
                        <td>{(crypto.supply / 1e6).toFixed(2)} M</td>
                        <td><Line data={{
                            labels: Array(10).fill(''),
                            datasets: [
                                {
                                    borderColor: crypto.change >= 0 ? 'green' : 'red',
                                    tension: 0.4,
                                    // label: 'My First dataset',
                                    // fill: false,
                                    // lineTension: 0.1,
                                    // backgroundColor: 'rgba(75,192,192,0.4)',
                                    // borderCapStyle: 'butt',
                                    // borderDash: [],
                                    // borderDashOffset: 0.0,
                                    // borderJoinStyle: 'miter',
                                    // pointBorderColor: 'rgba(75,192,192,1)',
                                    // pointBackgroundColor: '#fff',
                                    // pointBorderWidth: 1,
                                    // pointHoverRadius: 5,
                                    // pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                                    // pointHoverBorderColor: 'rgba(220,220,220,1)',
                                    // pointHoverBorderWidth: 2,
                                    // pointRadius: 1,
                                    // pointHitRadius: 10,
                                    data: Array(10).fill(crypto.price),
                                }
                            ],
                        }} options={{
                            responsive: true,
                            plugins: {
                                legend: {display: false},
                            },
                        }} 
                        width={100} height={30}/></td>
                        <td><button className="trade-btn">Trade</button></td>
                    </tr>
                ))}
            </tbody>
        </table>
      </div>
    );
  };

  export default CryptoTrack;
