import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import "./styles/App.css";

interface CryptoData {
  id: string;
  symbol: string;
  name: string;
  price: number;
  change: number;
  marketCap: number;
  volume24h: number;
  supply: number;
}

const CryptoTrack: React.FC = () => {
  const [cryptoData, setCryptoData] = useState<CryptoData[]>([]);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/crypto");
        setCryptoData(response.data);
      } catch (error) {
        console.error("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
    const interval = setInterval(fetchCryptoData, 10000); // Update every 10 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="CryptoTrack">
      <h2 className="title">Market</h2>
      <table>
        <thead>
          <tr>
            <th>Currency</th>
            <th>Price</th>
            <th>Change</th>
            <th>Market Cap</th>
            <th>Volume 24h</th>
            <th>Supply</th>
            <th>Chart</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {cryptoData.map((crypto) => (
            <tr key={crypto.id}>
              <td>{crypto.name}</td>
              <td>${crypto.price.toFixed(2)}</td>
              <td style={{ color: crypto.change >= 0 ? "green" : "red" }}>
                {crypto.change.toFixed(2)}%
              </td>
              <td>${(crypto.marketCap / 1e9).toFixed(2)} B</td>
              <td>${(crypto.volume24h / 1e9).toFixed(2)} B</td>
              <td>{(crypto.supply / 1e6).toFixed(2)} M</td>
              <td>
                <Line
                  data={{
                    labels: Array.from({ length: 10 }, (_, i) => i + 1),
                    datasets: [
                      {
                        label: "Price Trend",
                        data: Array(10).fill(crypto.price),
                        fill: false,
                        borderColor: crypto.change >= 0 ? "green" : "red",
                        tension: 0.1,
                      },
                    ],
                  }}
                  width={100}
                  height={50}
                />
              </td>
              <td>
                <button className="trade-btn">Trade</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CryptoTrack;
